customAction.customCss:
#edit-device-form form {
  background-color: var(--tb-service-background-0);
  border: 1px solid var(--tb-service-border-0);
  min-height: 70vh;
}
#edit-device-form input[type='number']::-webkit-outer-spin-button,
#edit-device-form input[type='number']::-webkit-inner-spin-button {
  -webkit-appearance: none;
  margin: 0;
}
#edit-device-form input[type='number'] {
  -moz-appearance: textfield;
}
#edit-device-form .dialog-title-box {
  padding: 2em;
  border-bottom: 1px solid var(--tb-service-border-1);
}
#edit-device-form .dialog-title {
  color: var(--tb-service-font-5);
  font-size: 1.6em;
}
#edit-device-form .dialog-close {
  all: unset;
  cursor: pointer;
  color: var(--tb-service-font-3);
  transition-property: color;
  transition-duration: var(--tb-config-color-duration);
}
#edit-device-form .dialog-close i {
  font-size: 1.4em;
}
#edit-device-form .dialog-close:hover {
  color: var(--tb-service-font-5);
}

#edit-device-form .step-controller {
  padding: 2em 4em;
}
#edit-device-form .step {
  position: relative;
  display: inline-block;
  padding: 0.3em;
  background-color: var(--tb-service-border-1);
  border: 1px solid var(--tb-service-border-1);
  color: var(--tb-service-border-1);
  transition-property: color, border-color, background-color;
  transition-duration: var(--tb-config-color-duration);
  border-radius: 50%;
  cursor: pointer;
}
#edit-device-form .step i {
  font-size: 1.4em;
}
#edit-device-form .step.prev {
  background-color: var(--tb-service-accent);
  border: 1px solid var(--tb-service-accent);
  color: var(--tb-service-font-0);
}
#edit-device-form .step.current {
  background-color: var(--tb-service-font-0);
  border: 1px solid var(--tb-service-accent);
  color: var(--tb-service-font-0);
}
#edit-device-form .step:after {
  content: attr(data-label);
  position: absolute;
  top: 125%;
  left: 50%;
  transform: translateX(-50%);
  color: var(--tb-service-font-3);
  white-space: nowrap;
  font-size: 1.2em;
}
#edit-device-form .step.current:after {
  color: var(--tb-service-font-5);
}
#edit-device-form .step-progress-line {
  width: 100%;
  height: 2px;
  background-color: var(--tb-service-border-1);
  transition-property: background-color;
  transition-duration: var(--tb-config-color-duration);
}
#edit-device-form .step-progress-line.prev {
  background-color: var(--tb-service-accent);
}

#edit-device-form .mat-dialog-content {
  padding: var(--tb-config-padding) !important;
}
#edit-device-form .mat-dialog-content::-webkit-scrollbar {
  width: 6px;
  height: 6px;
}
#edit-device-form .mat-dialog-content::-webkit-scrollbar-thumb {
  border: 2px solid #00000000;
  background-clip: padding-box;
  background-color: var(--tb-service-accent);
}
#edit-device-form .mat-dialog-content::-webkit-scrollbar-thumb:hover {
  border: 1px solid #00000000;
}

#edit-device-form .label-box {
  color: var(--tb-service-font-3);
  font-size: 1.2em;
  transition-property: color, background-color, border-color;
  transition-duration: var(--tb-config-color-duration);
}
#edit-device-form .input-box.disabled .label-box {
  color: var(--tb-service-font-1);
}
#edit-device-form .value-box {
  background-color: var(--tb-service-background-0);
  border: 1px solid var(--tb-service-border-0);
  transition-property: color, background-color, border-color;
  transition-duration: var(--tb-config-color-duration);
}
#edit-device-form .value-box.no-border {
  border: none !important;
}

#edit-device-form .input-box:not(.disabled) .value-box:hover {
  border-color: var(--tb-service-border-2);
}
#edit-device-form .input-box:not(.disabled) .value-box:focus-within {
  border-color: var(--tb-service-accent);
  caret-color: var(--tb-service-accent);
}
#edit-device-form .value-box *::placeholder {
  color: var(--tb-service-font-4);
}
#edit-device-form .value-box input {
  color: var(--tb-service-font-5);
  font-size: 1.4em;
  padding: calc(16em / 14);
  caret-color: var(--tb-service-accent);
}
#edit-device-form .input-box.disabled .value-box {
  background-color: var(--tb-service-background-1);
}
#edit-device-form .input-box.disabled .value-box input {
  color: var(--tb-service-font-2);
  cursor: default;
}
#edit-device-form .side-label {
  color: var(--tb-service-font-4);
  font-size: 1.4em;
}
#edit-device-form .switch-track {
  position: relative;
  width: 3.6em;
  height: 1.6em;
  border-radius: 0.8em;
  background-color: var(--tb-service-font-3);
  transition-property: background-color;
  transition-duration: var(--tb-config-color-duration);
  cursor: pointer;
}
#edit-device-form .switch-track.active {
  background-color: var(--tb-service-accent);
}
#edit-device-form .switch-ball {
  position: absolute;
  top: 50%;
  left: 0.8em;
  transform: translate(-50%, -50%);
  width: 1.2em;
  height: 1.2em;
  border-radius: 0.6em;
  background-color: var(--tb-service-background-0);
  transition-property: left;
  transition-duration: var(--tb-config-color-duration);
}
#edit-device-form .switch-track.active .switch-ball {
  left: calc(100% - 0.8em);
}
#edit-device-form .input-small {
  width: 3em;
}
#edit-device-form .unit-label {
  color: var(--tb-service-font-4);
  font-size: 1.4em;
}

#edit-device-form .value-box .mat-select-trigger {
  font-size: 1.4em;
  padding: calc(16em / 14);
  line-height: 1;
}
#edit-device-form .value-box .mat-select-value {
  font-family: var(--tb-config-font-family);
  font-weight: 300;
  font-size: 1em;
  color: var(--tb-service-font-5);
}
#edit-device-form .value-box .mat-select-arrow {
  font-family: var(--tb-config-font-family);
  font-weight: 300;
  border-left: 0.4em solid transparent;
  border-right: 0.4em solid transparent;
  border-top: 0.4em solid;
  color: var(--tb-service-accent);
}
#edit-device-form .error-box {
  display: none;
  color: var(--tb-service-warn);
  font-size: 1.2em;
}
#edit-device-form .input-box.error .label-box {
  color: var(--tb-service-warn);
}
#edit-device-form .input-box.error .value-box {
  border: 1px solid var(--tb-service-warn);
}
#edit-device-form .input-box.error .error-box {
  display: block;
  margin-top: calc(0.6em / 1.2);
}

#edit-device-form .model-container {
  position: relative;
  border: 1px solid var(--tb-service-border-1);
  background-color: var(--tb-service-background-1);
  cursor: pointer;
}
#edit-device-form .model-image {
  width: 10em;
  height: 10em;
  padding: 1em;
  background-color: var(--tb-service-background-1);
}
#edit-device-form .model-image img {
  width: 100%;
  height: 100%;
}
#edit-device-form .model-name {
  font-size: 1.6em;
  color: var(--tb-service-font-5);
}
#edit-device-form .model-label {
  font-size: 1.4em;
  color: var(--tb-service-font-4);
}
#edit-device-form .model-selector {
  position: absolute;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0);
  transition-property: background-color;
  transition-duration: var(--tb-config-color-duration);
}
#edit-device-form .model-selector i {
  color: var(--tb-service-accent);
  font-size: 4em;
  padding: 0.25em;
  border-radius: 50%;
  border: 0.1em solid var(--tb-service-accent);
  opacity: 0;
}
#edit-device-form .model-container.check .model-selector {
  background-color: rgba(0, 0, 0, 0.3);
}
#edit-device-form .model-container.check .model-selector i {
  opacity: 1;
}
#edit-device-form .model-container:not(.check) .model-selector:hover {
  background-color: rgba(0, 0, 0, 0.1);
}

#edit-device-form .mat-dialog-actions {
  padding: 2em;
  border-top: 1px solid var(--tb-service-border-1);
}
#edit-device-form .button {
  all: unset;
  padding: 0.6em 1.4em;
  font-size: 1.4em;
  box-sizing: border-box;
  cursor: pointer;
  transition-property: color, background-color, border-color;
  transition-duration: var(--tb-config-color-duration);
}
#edit-device-form .save-button,
#edit-device-form .next-button {
  color: var(--tb-service-font-0);
  background-color: var(--tb-service-accent);
}
#edit-device-form .save-button:hover,
#edit-device-form .next-button:hover {
  background-color: var(--tb-service-accent-hover);
}
#edit-device-form .save-button:active,
#edit-device-form .next-button:active {
  background-color: var(--tb-service-accent-pressed);
}
#edit-device-form .close-button,
#edit-device-form .prev-button {
  color: var(--tb-service-font-5);
  background-color: var(--tb-service-background-0);
  border: 1px solid var(--tb-service-border-0);
}
#edit-device-form .close-button:hover,
#edit-device-form .prev-button:hover {
  border-color: var(--tb-service-border-2);
}
#edit-device-form .close-button:active,
#edit-device-form .prev-button:active {
  background-color: var(--tb-service-border-0);
}

@media (min-width: 960px) {
  #edit-device-form form {
    width: 24vw;
  }
}

@media (max-width: 959px) {
  #edit-device-form form {
    width: 100%;
    height: 100%;
  }
  #edit-device-form .model-image {
    width: 30em;
    height: 30em;
  }
  #edit-device-form .model-name {
    font-size: 2.4em;
  }
  #edit-device-form .model-label {
    font-size: 2.1em;
  }
}

customAction.customFunction:
let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let deviceService = $injector.get(widgetContext.servicesMap.get('deviceService'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));

const t = widgetContext.custom.t;

openEditEntityDialog();

function openEditEntityDialog() {
  customDialog.customDialog(htmlTemplate, EditEntityDialogController).subscribe();
}

function EditEntityDialogController(instance) {
  let vm = instance;
  vm.ownerId = widgetContext.defaultSubscription.configuredDatasources[0].entity.id;
  vm.t = t;
  vm.modelList = [
    { check: false, name: t('thingplus.device-model.YMC-430.summary'), value: 'YMC-430' },
    { check: false, name: t('thingplus.device-model.M2-5AX_20K.summary'), value: 'M2-5AX_20K' },
    { check: false, name: t('thingplus.device-model.YBM-1218V.summary'), value: 'YBM-1218V' },
    { check: false, name: t('thingplus.device-model.RB-5M.summary'), value: 'RB-5M' },
    { check: false, name: t('thingplus.device-model.WINSTAR-SP.summary'), value: 'WINSTAR-SP' },
    { check: false, name: t('thingplus.device-model.UJG-35.summary'), value: 'UJG-35' },
    { check: false, name: t('thingplus.device-model.GLS-150GL.summary'), value: 'GLS-150GL' },
    { check: false, name: t('thingplus.device-model.AP450L.summary'), value: 'AP450L' },
    { check: false, name: t('thingplus.device-model.AQ1200L.summary'), value: 'AQ1200L' },
    { check: false, name: t('thingplus.device-model.AP3L.summary'), value: 'AP3L' },
    { check: false, name: t('thingplus.device-model.AG100L.summary'), value: 'AG100L' },
    { check: false, name: t('thingplus.device-model.TRULASER_CELL_7040.summary'), value: 'TRULASER_CELL_7040' },
    { check: false, name: t('thingplus.device-model.BC-3020_DYNAMIC.summary'), value: 'BC-3020_DYNAMIC' },
    { check: false, name: t('thingplus.device-model.DE4P-2500.summary'), value: 'DE4P-2500' },
    { check: false, name: t('thingplus.device-model.DE4P-2000.summary'), value: 'DE4P-2000' },
    { check: false, name: t('thingplus.device-model.DE2P-1200.summary'), value: 'DE2P-1200' },
    { check: false, name: t('thingplus.device-model.AUH-125H.summary'), value: 'AUH-125H' },
    { check: false, name: t('thingplus.device-model.ACCURA-II.summary'), value: 'ACCURA-II' },
    { check: false, name: t('thingplus.device-model.CRYSTA-APEX_S_122010.summary'), value: 'CRYSTA-APEX_S_122010' },
    { check: false, name: t('thingplus.device-model.LHM-V2.summary'), value: 'LHM-V2' },
  ];
  vm.currentStep = 0;
  for (let i in vm.modelList) {
    vm.modelList[i].image = `https://tp-resource.thingplus.net/service/moldmecca/model/${vm.modelList[i].value}.png`;
    if (vm.modelList[i].value == additionalParams.model) {
      vm.modelList[i].check = true;
    }
  }

  vm.editEntityFormGroup = vm.fb.group({
    name: [entityName],
    label: [entityLabel, [vm.validators.required]],
    model: [additionalParams.model],
  });

  getDevice(entityId.id).subscribe(device => {
    vm.originDevice = device;
  });

  vm.calcFontSize = function () {
    let originWidth = widgetContext.settings.widget.originWidth;
    if (widgetContext.isMobile) {
      originWidth = 960;
      if (widgetContext.width < 600) {
        originWidth = 600;
      }
    }
    let widgetFontSize = _.round((widgetContext.width / originWidth) * 10, 2);
    if (widgetFontSize < 6.25) {
      widgetFontSize = 6.25;
    }

    return widgetFontSize;
  };
  vm.cancel = function () {
    vm.dialogRef.close(null);
  };
  vm.save = function () {
    vm.editEntityFormGroup.markAsPristine();
    let taskList = [saveDevice(), saveAttribute()];
    widgetContext.rxjs.forkJoin(taskList).subscribe(() => {
      widgetContext.updateAliases();
      vm.dialogRef.close(null);
    });
  };
  vm.selectStep = function (e, index) {
    vm.currentStep = index;
  };
  vm.prevStep = function () {
    vm.currentStep--;
  };
  vm.nextStep = function () {
    vm.currentStep++;
  };
  vm.selectModel = function (model) {
    for (let i in vm.modelList) {
      vm.modelList[i].check = false;
      if (vm.modelList[i].value == model) {
        vm.modelList[i].check = true;
        vm.editEntityFormGroup.patchValue(
          {
            model: model,
          },
          { emitEvent: false }
        );
      }
    }
  };

  function getDevice(deviceId) {
    return deviceService.getDevice(deviceId);
  }

  function saveDevice() {
    let label = vm.editEntityFormGroup.get('label').value;

    if (_.isNil(vm.originDevice)) return;
    vm.originDevice.label = label;
    return deviceService.saveDevice(vm.originDevice);
  }

  function saveAttribute() {
    let model = vm.editEntityFormGroup.get('model').value;

    let attributeBody = [{ key: 'model', value: model }];
    return attributeService.saveEntityAttributes(entityId, 'SERVER_SCOPE', attributeBody);
  }
}
