settings.cellContentFunction:
if(value){
    let target = JSON.parse(value)
    if(target.isActive){
        return target.trialMin + ' ~ ' + target.trialMax + ctx.custom.t('thingplus.time-format.minute')
    }
}
return '-'

if(value == 'WAIT'){
    return ctx.custom.t('thingplus.state.waiting');
}
if(value == 'STOP'){
    return ctx.custom.t('thingplus.state.stopped');
}
return '-'

if(value == 'true'){
    return `<div class="activate-switch active"><div class="ball"></div></div>`
}
return `<div class="activate-switch"><div class="ball"></div></div>`

settings.cellActionFunction:
let descriptors = ctx.actionsApi.getActionDescriptors('customAction');
let targetIndex = descriptors.findIndex(x => x.name == 'Activate');
if (targetIndex != -1) {
  ctx.actionsApi.handleWidgetAction(
    {},
    descriptors[targetIndex],
    ctx.custom.mainData[tr.index].id,
    ctx.custom.mainData[tr.index].name,
    ctx.custom.mainData[tr.index],
    ctx.custom.mainData[tr.index].label
  );
}

widgetHeaderButton.customCss:
#add-device-form form {
  background-color: var(--tb-service-background-0);
  border: 1px solid var(--tb-service-border-0);
  min-height: 70vh;
}
#add-device-form input[type='number']::-webkit-outer-spin-button,
#add-device-form input[type='number']::-webkit-inner-spin-button {
  -webkit-appearance: none;
  margin: 0;
}
#add-device-form input[type='number'] {
  -moz-appearance: textfield;
}
#add-device-form .dialog-title-box {
  padding: 2em;
  border-bottom: 1px solid var(--tb-service-border-1);
}
#add-device-form .dialog-title {
  color: var(--tb-service-font-5);
  font-size: 1.6em;
}
#add-device-form .dialog-close {
  all: unset;
  cursor: pointer;
  color: var(--tb-service-font-3);
  transition-property: color;
  transition-duration: var(--tb-config-color-duration);
}
#add-device-form .dialog-close i {
  font-size: 1.4em;
}
#add-device-form .dialog-close:hover {
  color: var(--tb-service-font-5);
}

#add-device-form .step-controller {
  padding: 2em 4em;
}
#add-device-form .step {
  position: relative;
  display: inline-block;
  padding: 0.3em;
  background-color: var(--tb-service-border-1);
  border: 1px solid var(--tb-service-border-1);
  color: var(--tb-service-border-1);
  transition-property: color, border-color, background-color;
  transition-duration: var(--tb-config-color-duration);
  border-radius: 50%;
  cursor: pointer;
}
#add-device-form .step i {
  font-size: 1.4em;
}
#add-device-form .step.prev {
  background-color: var(--tb-service-accent);
  border: 0.1em solid var(--tb-service-accent);
  color: var(--tb-service-font-0);
}
#add-device-form .step.current {
  background-color: var(--tb-service-font-0);
  border: 0.3em solid var(--tb-service-accent);
  color: var(--tb-service-font-0);
}
#add-device-form .step:after {
  content: attr(data-label);
  position: absolute;
  top: 125%;
  left: 50%;
  transform: translateX(-50%);
  color: var(--tb-service-font-3);
  white-space: nowrap;
  font-size: 1.2em;
}
#add-device-form .step.current:after {
  color: var(--tb-service-font-5);
}
#add-device-form .step-progress-line {
  width: 100%;
  height: 0.2em;
  background-color: var(--tb-service-border-1);
  transition-property: background-color;
  transition-duration: var(--tb-config-color-duration);
}
#add-device-form .step-progress-line.prev {
  background-color: var(--tb-service-accent);
}

#add-device-form .mat-dialog-content {
