}
#add-device-form .mat-dialog-content::-webkit-scrollbar {
  width: 6px;
  height: 6px;
}
#add-device-form .mat-dialog-content::-webkit-scrollbar-thumb {
  border: 2px solid #00000000;
  background-clip: padding-box;
  background-color: var(--tb-service-accent);
}
#add-device-form .mat-dialog-content::-webkit-scrollbar-thumb:hover {
  border: 1px solid #00000000;
}

#add-device-form .header-row,
#add-device-form .sub-row {
