}
#add-device-form .input-box.disabled .value-box input {
  color: var(--tb-service-font-2);
  cursor: default;
}
#add-device-form .side-label {
  color: var(--tb-service-font-4);
  font-size: 1.4em;
}
#add-device-form .switch-track {
  position: relative;
  width: 3.6em;
  height: 1.6em;
  border-radius: 0.8em;
  background-color: var(--tb-service-border-1);
  transition-property: background-color;
  transition-duration: var(--tb-config-color-duration);
  cursor: pointer;
}
#add-device-form .switch-track.active {
  background-color: var(--tb-service-accent);
}
#add-device-form .switch-ball {
  position: absolute;
  top: 50%;
  left: 0.8em;
  transform: translate(-50%, -50%);
  width: 1.2em;
  height: 1.2em;
  border-radius: 0.6em;
  background-color: var(--tb-service-background-0);
  transition-property: left;
  transition-duration: var(--tb-config-color-duration);
}
#add-device-form .switch-track.active .switch-ball {
  left: calc(100% - 0.8em);
}
#add-device-form .radio-box .radio-circle {
  width: 2em;
  height: 2em;
  border-radius: 50%;
  border: 1px solid var(--tb-service-border-1);
}
#add-device-form .radio-box .radio-circle {
  width: 2em;
  height: 2em;
  border-radius: 50%;
  border: 0.1em solid var(--tb-service-border-1);
}
#add-device-form .radio-box.active .radio-circle {
  width: 2em;
  height: 2em;
  border: 0.6em solid var(--tb-service-accent);
}
#add-device-form .radio-box .radio-label {
  font-size: 1.4em;
  color: var(--tb-service-font-4);
}
#add-device-form .input-small {
  width: 2em;
}
#add-device-form .unit-label {
  color: var(--tb-service-font-4);
  font-size: 1.4em;
}

#add-device-form .value-box .mat-select-trigger {
  font-size: 1.4em;
  padding: calc(16em / 14);
  line-height: 1;
}
#add-device-form .value-box .mat-select-value {
  font-family: var(--tb-config-font-family);
  font-weight: 300;
  font-size: 1em;
  color: var(--tb-service-font-5);
}
#add-device-form .value-box .mat-select-arrow {
  font-family: var(--tb-config-font-family);
  font-weight: 300;
  border-left: 0.4em solid transparent;
  border-right: 0.4em solid transparent;
  border-top: 0.4em solid;
  color: var(--tb-service-accent);
}
#add-device-form .description-box {
  margin-top: calc(0.6em / 1.2);
  color: var(--tb-service-font-3);
  font-size: 1.2em;
}
#add-device-form .description-box b {
  color: var(--tb-service-accent);
}
#add-device-form .error-box {
  display: none;
  color: var(--tb-service-warn);
  font-size: 1.2em;
}
#add-device-form .input-box.error .label-box {
  color: var(--tb-service-warn);
}
#add-device-form .input-box.error .value-box {
  border: 1px solid var(--tb-service-warn);
}
#add-device-form .input-box.error .description-box {
  display: none;
}
#add-device-form .input-box.error .error-box {
  display: block;
  margin-top: calc(0.6em / 1.2);
}

#add-device-form .mat-dialog-actions {
  padding: 2em;
  border-top: 1px solid var(--tb-service-border-1);
}
#add-device-form .button {
  all: unset;
  padding: 0.6em 1.4em;
  font-size: 1.4em;
  box-sizing: border-box;
  cursor: pointer;
  transition-property: color, background-color, border-color;
  transition-duration: var(--tb-config-color-duration);
}
#add-device-form .save-button,
#add-device-form .next-button {
