settings.cellContentFunction:
let dashboards = {
    'Report': ctx.custom.t('thingplus.page.report.entire') + ' : ' + ctx.custom.t('thingplus.page.report.entire-report'),
    'Total Work Report': ctx.custom.t('thingplus.page.report.entire') + ' : ' + ctx.custom.t('thingplus.page.report.total-work-report'),
    'Cost Report': ctx.custom.t('thingplus.page.report.entire') + ' : ' + ctx.custom.t('thingplus.page.report.cost-report'),
    'Work Report': ctx.custom.t('thingplus.page.report.single') + ' : ' + ctx.custom.t('thingplus.page.report.work-report'),
    'Dalay Report': ctx.custom.t('thingplus.page.report.single') + ' : ' + ctx.custom.t('thingplus.page.report.delay-report')
}
return dashboards[value];

if(value == ''){
    return ctx.custom.t('thingplus.selector.entire-customerL1')
}
let target = ctx.custom.relations[value]
if(target){
    return target.name
}
return value

if(value == ''){
    return ctx.custom.t('thingplus.selector.entire-customerL2')
}
let target = ctx.custom.relations[value]
if(target){
    return target.name
}
return value

if(row.reportCategory != 'single'){
    return ctx.custom.t('thingplus.selector.entire-device');
}
let devices = value.split(',')
if(devices.length == 0){
    return '-';
}
if(devices.length == 1){
    return ctx.custom.relations[devices[0]].label;
}
return ctx.custom.relations[devices[0]].label + ' ' + ctx.custom.t('thingplus.label.and-other', {count: devices.length - 1})

if(value == 'DAILY'){
    return ctx.custom.t('thingplus.time-format.day-by-day');
}else if(value == 'WEEKLY'){
    return ctx.custom.t('thingplus.time-format.week-by-week');
}else if(value == 'MONTHLY'){
    return ctx.custom.t('thingplus.time-format.month-by-month');
}
return ctx.custom.t('thingplus.label.custom-input');

let days = ['mon', 'tue', 'wed', 'thu', 'fri', 'sat', 'sun'];
if (row.reportType == 'DAILY') {
  let target = value.split(',');
  for (let i in target) {
    if (target[i]) {
      target[i] = ctx.custom.t('thingplus.time-format.weekday-short.' + days[target[i]]);
    }
  }
  return target.join(', ');
}
if (row.reportType == 'WEEKLY') {
  return ctx.custom.t('thingplus.time-format.weekday-long.' + days[value]);
}
if (row.reportType == 'MONTHLY') {
  return ctx.custom.t('thingplus.time-format.day-value', { day: value });
}
if (row.reportType == 'CUSTOM') {
  return ctx.custom.t('thingplus.time-format.day-interval', { day: value });
}
return value;

widgetHeaderButton.customCss:
#add-report-form form {
  background-color: var(--tb-service-background-0);
  border: 1px solid var(--tb-service-border-0);
  min-height: 70vh;
}
#add-report-form .dialog-title-box {
  padding: 2em;
  border-bottom: 1px solid var(--tb-service-border-1);
}
#add-report-form .dialog-title {
  color: var(--tb-service-font-5);
  font-size: 1.6em;
}
#add-report-form .dialog-close {
  all: unset;
  cursor: pointer;
  color: var(--tb-service-font-3);
  transition-property: color;
  transition-duration: var(--tb-config-color-duration);
}
#add-report-form .dialog-close i {
  font-size: 1.4em;
}
#add-report-form .dialog-close:hover {
  color: var(--tb-service-font-5);
}

#add-report-form .step-controller {
  padding: 4em;
}
#add-report-form .step {
  position: relative;
  display: inline-block;
  padding: 0.3em;
  background-color: var(--tb-service-border-1);
  border: 1px solid var(--tb-service-border-1);
  color: var(--tb-service-border-1);
  transition-property: color, border-color, background-color;
  transition-duration: var(--tb-config-color-duration);
  border-radius: 50%;
  cursor: pointer;
}
#add-report-form .step i {
  font-size: 1.4em;
}
#add-report-form .step.prev {
  background-color: var(--tb-service-accent);
  border: 1px solid var(--tb-service-accent);
  color: var(--tb-service-font-0);
}
#add-report-form .step.current {
  background-color: var(--tb-service-font-0);
  border: 1px solid var(--tb-service-accent);
  color: var(--tb-service-font-0);
}
#add-report-form .step:after {
  content: attr(data-label);
  position: absolute;
  top: 125%;
  left: 50%;
  transform: translateX(-50%);
  color: var(--tb-service-font-3);
  white-space: nowrap;
  font-size: 1.2em;
}
#add-report-form .step.current:after {
  color: var(--tb-service-font-5);
}
#add-report-form .step-progress-line {
  width: 100%;
  height: 2px;
  background-color: var(--tb-service-border-1);
  transition-property: background-color;
  transition-duration: var(--tb-config-color-duration);
}
#add-report-form .step-progress-line.prev {
  background-color: var(--tb-service-accent);
}

#add-report-form .mat-dialog-content {
  padding: var(--tb-config-padding) !important;
}
#add-report-form .mat-dialog-content::-webkit-scrollbar {
  width: 6px;
  height: 6px;
}
#add-report-form .mat-dialog-content::-webkit-scrollbar-thumb {
  border: 2px solid #00000000;
  background-clip: padding-box;
  background-color: var(--tb-service-accent);
}
#add-report-form .mat-dialog-content::-webkit-scrollbar-thumb:hover {
  border: 1px solid #00000000;
}

#add-report-form .label-box {
  color: var(--tb-service-font-3);
  font-size: 1.2em;
  transition-property: color, background-color, border-color;
  transition-duration: var(--tb-config-color-duration);
}
#add-report-form .input-box.disabled .label-box {
  color: var(--tb-service-font-1);
}
#add-report-form .value-box {
  background-color: var(--tb-service-background-0);
  border: 1px solid var(--tb-service-border-0);
  transition-property: color, background-color, border-color;
  transition-duration: var(--tb-config-color-duration);
}
#add-report-form .value-box.no-border {
  border: none !important;
}

#add-report-form .input-box:not(.disabled) .value-box:hover {
  border-color: var(--tb-service-border-3);
}
#add-report-form .input-box:not(.disabled) .value-box:focus-within {
  border-color: var(--tb-service-accent);
  caret-color: var(--tb-service-accent);
}
#add-report-form .value-box *::placeholder {
  color: var(--tb-service-font-4);
}
#add-report-form .value-box input {
  color: var(--tb-service-font-5);
  font-size: 1.4em;
  padding: calc(16em / 14);
  caret-color: var(--tb-service-accent);
}
#add-report-form .input-box.disabled .value-box {
  background-color: var(--tb-service-background-1);
}
#add-report-form .input-box.disabled .value-box input {
  color: var(--tb-service-font-2);
  cursor: default;
}
#add-report-form .radio-btn {
  all: unset;
  color: var(--tb-service-font-5);
  border: 1px solid var(--tb-service-border-0);
  font-size: 1.4em;
  padding: calc(16em / 14);
  text-align: center;
  cursor: pointer;
}
#add-report-form .input-box:not(.disabled) .radio-btn:not(.active):hover {
  border-color: var(--tb-service-border-3);
}
#add-report-form .input-box:not(.disabled) .radio-btn.active {
  color: var(--tb-service-accent);
  border-color: var(--tb-service-accent);
}
#add-report-form .input-box.disabled .radio-btn:not(.active) {
  color: var(--tb-service-font-1);
  cursor: default;
}
#add-report-form .input-box.disabled .radio-btn.active {
  border-color: var(--tb-service-border-3);
  cursor: default;
}

#add-report-form .grid-box {
  gap: 1em;
  max-width: 100%;
}
#add-report-form .chip {
  border: 1px solid var(--tb-service-border-0);
  color: var(--tb-service-font-5);
  padding: 1em;
  overflow: hidden;
}
#add-report-form .chip-label {
  font-size: 1.4em;
  overflow: hidden;
  text-overflow: ellipsis;
}
#add-report-form .email-add-button {
  border: none;
  padding: 0.4em 1em;
  margin-right: calc(16em / 14);
  font-size: 1.4em;
  color: var(--tb-service-accent);
  border: 1px solid var(--tb-service-accent);
  background-color: var(--tb-service-background-0);
  cursor: pointer;
  transition-property: color, border-color;
  transition-duration: var(--tb-config-color-duration);
}
#add-report-form .input-box.error .email-add-button {
  cursor: default;
  color: var(--tb-service-disabled);
  border-color: var(--tb-service-disabled);
}
#add-report-form .input-box:not(.error) .email-add-button:hover {
  color: var(--tb-service-accent-hover);
  border-color: var(--tb-service-accent-hover);
}
#add-report-form .input-box:not(.error) .email-add-button:active {
  color: var(--tb-service-accent-pressed);
  border-color: var(--tb-service-accent-pressed);
}
#add-report-form .email-delete-button {
  border: none;
  padding: 0.1em;
  font-size: 1.4em;
  color: var(--tb-service-font-0);
  background-color: var(--tb-service-font-1);
  border-radius: 50%;
  cursor: pointer;
  transition-property: color, background-color;
  transition-duration: var(--tb-config-color-duration);
}
#add-report-form .email-delete-button:hover {
  background-color: var(--tb-service-font-2);
}
#add-report-form .email-delete-button:active {
  background-color: var(--tb-service-font-3);
}

#add-report-form .value-box .mat-select-trigger {
  font-size: 1.4em;
  padding: calc(16em / 14);
  line-height: 1;
}
#add-report-form .value-box .mat-select-value {
  font-family: var(--tb-config-font-family);
  font-weight: 300;
  font-size: 1em;
  color: var(--tb-service-font-5);
}
#add-report-form .value-box .mat-select-arrow {
  font-family: var(--tb-config-font-family);
  font-weight: 300;
  border-left: 0.4em solid transparent;
  border-right: 0.4em solid transparent;
  border-top: 0.4em solid;
  color: var(--tb-service-accent);
}
#add-report-form .group-box {
  border: 1px solid var(--tb-service-border-0);
  padding: 1.6em;
}
#add-report-form .mat-checkbox.mat-checkbox-checked .mat-checkbox-background {
  background-color: var(--tb-service-accent);
}
#add-report-form .mat-checkbox-checkmark-path {
  stroke: var(--tb-service-font-0) !important;
}
#add-report-form .mat-checkbox .mat-checkbox-inner-container {
  width: 1.8em;
  height: 1.8em;
}
#add-report-form .mat-checkbox .mat-checkbox-label {
  color: var(--tb-service-font-3);
  font-size: 1.4em;
}
#add-report-form .mat-checkbox .mat-checkbox-frame {
  border-color: var(--tb-service-border-0);
}
#add-report-form .mat-checkbox.mat-checkbox-checked .mat-checkbox-frame {
  border-color: var(--tb-service-accent);
}
#add-report-form .error-box {
  color: var(--tb-service-warn);
  font-size: 1.2em;
  margin-top: calc(0.6em / 1.2);
}
#add-report-form .input-box.error .label-box {
  color: var(--tb-service-warn);
}
#add-report-form .input-box.error .value-box {
  border: 1px solid var(--tb-service-warn);
}
#add-report-form .input-box.error .error-box {
  display: block;
  margin-top: calc(0.6em / 1.2);
}

#add-report-form .mat-dialog-actions {
  padding: 2em;
  border-top: 1px solid var(--tb-service-border-1);
}
#add-report-form .button {
  all: unset;
  padding: 0.6em 1.4em;
  font-size: 1.4em;
  box-sizing: border-box;
  cursor: pointer;
  transition-property: color, background-color, border-color;
  transition-duration: var(--tb-config-color-duration);
}
#add-report-form .save-button,
#add-report-form .next-button {
  color: var(--tb-service-font-0);
  background-color: var(--tb-service-accent);
}
#add-report-form .save-button:hover,
#add-report-form .next-button:hover {
  background-color: var(--tb-service-accent-hover);
}
#add-report-form .save-button:active,
#add-report-form .next-button:active {
  background-color: var(--tb-service-accent-pressed);
}
#add-report-form .close-button,
#add-report-form .prev-button {
  color: var(--tb-service-font-5);
  background-color: var(--tb-service-background-0);
  border: 1px solid var(--tb-service-border-0);
}
#add-report-form .close-button:hover,
#add-report-form .prev-button:hover {
  border-color: var(--tb-service-border-3);
}
#add-report-form .close-button:active,
#add-report-form .prev-button:active {
  background-color: var(--tb-service-border-0);
}

@media (min-width: 960px) {
  #add-report-form form {
    width: 24vw;
  }
}

widgetHeaderButton.customFunction:
let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let assetService = $injector.get(widgetContext.servicesMap.get('assetService'));
let entityRelationService = $injector.get(widgetContext.servicesMap.get('entityRelationService'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));

const t = widgetContext.custom.t;

openAddEntityDialog();

function openAddEntityDialog() {
  customDialog.customDialog(htmlTemplate, AddEntityDialogController).subscribe();
}

function AddEntityDialogController(instance) {
  let vm = instance;
  vm.ownerId = widgetContext.defaultSubscription.configuredDatasources[0].entity.id;
  vm.ownerLevel = widgetContext.$scope.ownerLevel;
  vm.t = t;
  vm.currentStep = 0;
  vm.newEmail = '';
  vm.emailList = [];
  vm.isSingleReport = false;
  vm.customerL1List = [{ name: t('thingplus.selector.entire-customerL1'), value: '' }];
  vm.customerL2List = [{ name: t('thingplus.selector.entire-customerL2'), value: '' }];
  vm.deviceList = [{ name: t('thingplus.selector.select-all'), value: 'ENTIRE' }];
  vm.userList = [{ name: t('thingplus.selector.select-all'), value: 'ENTIRE', email: '' }];
  if (vm.ownerLevel == 0) {
    vm.customerL1List = vm.customerL1List.concat(
      widgetContext.custom.customerL1List
        .map(x => {
          return {
            name: x.name,
            value: x.id.id,
          };
        })
        .sort((a, b) => {
          if (a.name > b.name) return 1;
          if (a.name < b.name) return -1;
          return 0;
        })
    );
  }
  if (vm.ownerLevel <= 1) {
    vm.customerL2List = vm.customerL2List.concat(
      widgetContext.custom.customerL2List
        .map(x => {
          return {
            name: x.name,
            value: x.id.id,
          };
        })
        .sort((a, b) => {
          if (a.name > b.name) return 1;
          if (a.name < b.name) return -1;
          return 0;
        })
    );
  }
  vm.deviceList = vm.deviceList.concat(
    widgetContext.custom.deviceList
      .map(x => {
        return {
          name: x.label,
          value: x.id.id,
        };
      })
      .sort((a, b) => {
        if (a.name > b.name) return 1;
        if (a.name < b.name) return -1;
        return 0;
      })
  );
  vm.userList = vm.userList.concat(
    widgetContext.custom.userList
      .map(x => {
        return {
          name: x.details.firstName,
          value: x.id.id,
          email: x.details.email,
        };
      })
      .sort((a, b) => {
        if (a.name > b.name) return 1;
        if (a.name < b.name) return -1;
        return 0;
      })
  );
  vm.originDevices = [];
  vm.originUsers = [];
  vm.reportCategoryList = [
    { name: t('thingplus.page.report.report-entire'), value: 'entire' },
    { name: t('thingplus.page.report.report-single'), value: 'single' },
  ];
  vm.reportDashboardList = [
    { name: t('thingplus.page.report.entire-report'), value: 'Report' },
    { name: t('thingplus.page.report.total-work-report'), value: 'Total Work Report' },
    { name: t('thingplus.page.report.cost-report'), value: 'Cost Report' },
  ];
  vm.dayList = [
    { name: t('thingplus.time-format.weekday-long.mon'), value: 0 },
    { name: t('thingplus.time-format.weekday-long.tue'), value: 1 },
    { name: t('thingplus.time-format.weekday-long.wed'), value: 2 },
    { name: t('thingplus.time-format.weekday-long.thu'), value: 3 },
    { name: t('thingplus.time-format.weekday-long.fri'), value: 4 },
    { name: t('thingplus.time-format.weekday-long.sat'), value: 5 },
    { name: t('thingplus.time-format.weekday-long.sun'), value: 6 },
  ];
  vm.dateList = [];
  for (let i = 1; i <= 31; i++) {
    vm.dateList.push({ name: t('thingplus.time-format.day-value', { day: i }), value: i });
  }
  let defaultCustomerL1Name = widgetContext.custom.relations[entityId.id].parent
    ? widgetContext.custom.relations[entityId.id].parent.name
    : entityName;
  vm.addEntityFormGroup = vm.fb.group({
    title: ['', [vm.validators.required]],
    reportCategory: ['entire'],
    reportDashboard: ['Report'],
    customerL1Name: [defaultCustomerL1Name],
    customerL2Name: [entityName],
    device: [vm.originDevices],
    reportType: ['DAILY'],
    reportTime: [moment().toDate()],
    dailyContent: vm.fb.group({
      0: [true],
      1: [true],
      2: [true],
      3: [true],
      4: [true],
      5: [true],
      6: [true],
    }),
    weeklyContent: [0],
    monthlyContent: [1],
    customContent: [2],
    user: [vm.originUsers],
    newEmail: [''],
  });
  if (vm.ownerLevel == 0) {
    vm.addEntityFormGroup.controls.customerL1 = vm.fb.control('');
  }
  if (vm.ownerLevel <= 1) {
    vm.addEntityFormGroup.controls.customerL2 = vm.fb.control('');
  }

  vm.calcFontSize = function () {
    const originWidth = widgetContext.settings.widget.originWidth;
    let widgetFontSize = _.round((widgetContext.width / originWidth) * 10, 2);
    if (widgetFontSize < 6.25) {
      widgetFontSize = 6.25;
    }
    return widgetFontSize;
  };
  vm.cancel = function () {
    vm.dialogRef.close(null);
  };
  vm.save = function () {
    vm.addEntityFormGroup.markAsPristine();
    let users = vm.addEntityFormGroup.get('user').value;
    if (vm.emailList.length == 0 && users.length == 0) {
      window.alert(t('thingplus.help.error-required-receiver'));
      return;
    }
    saveAsset().subscribe(asset => {
      widgetContext.rxjs
        .forkJoin([changeName(asset), assignAsset(asset), saveRelation(asset), saveAttribute(asset)])
        .subscribe(() => {
          widgetContext.updateAliases();
          vm.dialogRef.close(null);
        });
    });
  };
  vm.selectStep = function (e, index) {
    vm.currentStep = index;
  };
  vm.prevStep = function () {
    vm.currentStep--;
  };
  vm.nextStep = function () {
    vm.currentStep++;
  };
  vm.setCustomerL1 = function (e) {
    let customerL1 = vm.addEntityFormGroup.get('customerL1').value;
    vm.customerL2List = [{ name: t('thingplus.selector.entire-customerL2'), value: '' }];
    vm.deviceList = [{ name: t('thingplus.selector.select-all'), value: 'ENTIRE' }];
    vm.addEntityFormGroup.patchValue(
      {
        customerL2: '',
        device: [],
      },
      { emitEvent: false }
    );
    vm.customerL2List = vm.customerL2List.concat(
      widgetContext.custom.customerL2List
        .filter(x => {
          return customerL1 == '' || x.parent.id.id == customerL1;
        })
        .map(x => {
          return {
            name: x.name,
            value: x.id.id,
          };
        })
        .sort((a, b) => {
          if (a.name > b.name) return 1;
          if (a.name < b.name) return -1;
          return 0;
        })
    );
    vm.deviceList = vm.deviceList.concat(
      widgetContext.custom.deviceList
        .filter(x => {
          return customerL1 == '' || x.parent.parent.id.id == customerL1;
        })
        .map(x => {
          return {
            name: x.label,
            value: x.id.id,
          };
        })
        .sort((a, b) => {
          if (a.name > b.name) return 1;
          if (a.name < b.name) return -1;
          return 0;
        })
    );
  };
  vm.setCustomerL2 = function (e) {
    let customerL1 = vm.addEntityFormGroup.get('customerL1').value;
    let customerL2 = vm.addEntityFormGroup.get('customerL2').value;
    vm.deviceList = [{ name: t('thingplus.selector.select-all'), value: 'ENTIRE' }];
    vm.addEntityFormGroup.patchValue(
      {
        device: [],
      },
      { emitEvent: false }
    );
    vm.deviceList = vm.deviceList.concat(
      widgetContext.custom.deviceList
        .filter(x => {
          return (
            (customerL2 == '' && (customerL1 == '' || x.parent.parent.id.id == customerL1)) ||
            x.parent.id.id == customerL2
          );
        })
        .map(x => {
          return {
            name: x.label,
            value: x.id.id,
          };
        })
        .sort((a, b) => {
          if (a.name > b.name) return 1;
          if (a.name < b.name) return -1;
          return 0;
        })
    );
  };
  vm.setDevice = function (e) {
    //  Entire   
    let newValue = _.cloneDeep(e.value);
    if (vm.originDevices.includes('ENTIRE')) {
      // Entire     
      if (!e.value.includes('ENTIRE')) {
        newValue = [];
        // Entire     Entire 
      } else if (e.value.length < vm.originDevices.length) {
        newValue = e.value.filter(x => x != 'ENTIRE');
      }
      //  Entire    
    } else {
      // Entire    
      if (e.value.includes('ENTIRE')) {
        newValue = vm.deviceList.map(x => x.value);
      }
    }
    vm.addEntityFormGroup.patchValue(
      {
        device: newValue,
      },
      { emitEvent: false }
    );
    vm.originDevices = newValue;
  };
  vm.setUser = function (e) {
    //  Entire   
    let newValue = _.cloneDeep(e.value);
    if (vm.originUsers.includes('ENTIRE')) {
      // Entire     
      if (!e.value.includes('ENTIRE')) {
        newValue = [];
        // Entire     Entire 
      } else if (e.value.length < vm.originUsers.length) {
        newValue = e.value.filter(x => x != 'ENTIRE');
      }
      //  Entire    
    } else {
      // Entire    
      if (e.value.includes('ENTIRE')) {
        newValue = vm.userList.map(x => x.value);
      }
    }
    vm.addEntityFormGroup.patchValue(
      {
        user: newValue,
      },
      { emitEvent: false }
    );
    vm.originUsers = newValue;
  };
  vm.setReportCategory = function (e) {
    if (e.value == 'single') {
      vm.isSingleReport = true;
      vm.reportDashboardList = [
        { name: t('thingplus.page.report.work-report'), value: 'Work Report' },
        { name: t('thingplus.page.report.delay-report'), value: 'Delay Report' },
      ];
      vm.addEntityFormGroup.patchValue(
        {
          reportDashboard: 'Work Report',
        },
        { emitEvent: false }
      );
    } else if (e.value == 'entire') {
      vm.isSingleReport = false;
      vm.reportDashboardList = [
        { name: t('thingplus.page.report.entire-report'), value: 'Report' },
        { name: t('thingplus.page.report.total-work-report'), value: 'Total Work Report' },
        { name: t('thingplus.page.report.cost-report'), value: 'Cost Report' },
      ];
      vm.addEntityFormGroup.patchValue(
        {
          reportDashboard: 'Report',
        },
        { emitEvent: false }
      );
    }
  };
  vm.setReportType = function (e, type) {
    vm.addEntityFormGroup.patchValue(
      {
        reportType: type,
      },
      { emitEvent: false }
    );
  };
  vm.addEmail = function (e) {
    let newEmail = vm.addEntityFormGroup.get('newEmail');
    if (newEmail.hasError('email')) return;
    if (newEmail.value != '') {
      vm.emailList.push(newEmail.value);
      vm.addEntityFormGroup.patchValue(
        {
          newEmail: '',
        },
        { emitEvent: false }
      );
    }
  };
  vm.deleteEmail = function (e, email) {
    let targetIndex = vm.emailList.indexOf(email);
    vm.emailList.splice(targetIndex, 1);
  };

  function saveAsset() {
    let name = moment().valueOf() + '-' + Math.floor(1000000000000 * Math.random());
    let body = {
      name: name,
      label: vm.addEntityFormGroup.get('title').value,
      type: 'report',
    };
    return assetService.saveAsset(body);
  }
  function changeName(asset) {
    asset.name = asset.id.id;
    return assetService.saveAsset(asset);
  }

  function assignAsset(asset) {
    if (vm.ownerLevel > 0) {
      return assetService.assignAssetToCustomer(entityId.id, asset.id.id);
    } else {
      return widgetContext.rxjs.of([]);
    }
  }

  function saveRelation(asset) {
    let relationBody = {
      from: entityId,
      to: asset.id,
      type: 'Reports',
    };
    return entityRelationService.saveRelation(relationBody);
  }

  function saveAttribute(asset) {
    let title = vm.addEntityFormGroup.get('title').value;
    let reportCategory = vm.addEntityFormGroup.get('reportCategory').value;
    let reportDashboard = vm.addEntityFormGroup.get('reportDashboard').value;
    let customerL1Id = '',
      customerL2Id = '';
    if (vm.ownerLevel == 0) {
      customerL1Id = vm.addEntityFormGroup.get('customerL1').value;
      customerL2Id = vm.addEntityFormGroup.get('customerL2').value;
    } else if (vm.ownerLevel == 1) {
      customerL1Id = entityId.id;
      customerL2Id = vm.addEntityFormGroup.get('customerL2').value;
    } else {
      customerL2Id = entityId.id;
      customerL1Id = widgetContext.custom.relations[customerL2Id].parent.id.id;
    }
    let reportType = vm.addEntityFormGroup.get('reportType').value;
    let reportTime = vm.addEntityFormGroup.get('reportTime').value;
    reportTime = moment(reportTime).hour() * 3600000 + moment(reportTime).minute() * 60000;
    let reportContent = '';
    if (reportType == 'DAILY') {
      reportContent = vm.addEntityFormGroup.get('dailyContent').value;
      let result = [];
      for (let i in reportContent) {
        if (reportContent[i]) {
          result.push(i);
        }
      }
      reportContent = result.join(',');
    } else if (reportType == 'WEEKLY') {
      reportContent = vm.addEntityFormGroup.get('weeklyContent').value;
    } else if (reportType == 'MONTHLY') {
      reportContent = vm.addEntityFormGroup.get('monthlyContent').value;
    } else {
      reportContent = vm.addEntityFormGroup.get('customContent').value;
    }
    let reportPage = [
      {
        dashboardId: widgetContext.custom.dashboardList[reportDashboard].id.id,
        stateId: 'default',
      },
    ];

    let users = vm.addEntityFormGroup.get('user').value;
    users = users.filter(x => x != 'ENTIRE');

    let emailList = vm.userList.filter(x => users.includes(x.value)).map(x => x.email);
    emailList = emailList.concat(vm.emailList);
    emailList = _.uniq(emailList);

    let attributeBody = [
      { key: 'name', value: title },
      { key: 'reportCategory', value: reportCategory },
      { key: 'reportDashboard', value: reportDashboard },
      { key: 'customerL1', value: customerL1Id },
      { key: 'customerL2', value: customerL2Id },
      { key: 'reportType', value: reportType },
      { key: 'reportTime', value: reportTime },
      { key: 'reportContent', value: reportContent },
      { key: 'reportPage', value: reportPage },
      { key: 'reportUpdateTime', value: moment().valueOf() },
      { key: 'receiver', value: emailList.join(',') },
    ];

    if (vm.isSingleReport) {
      let deviceIds = vm.addEntityFormGroup.get('device').value;
      deviceIds = deviceIds.filter(x => x != 'ENTIRE');
      attributeBody.push({ key: 'device', value: deviceIds.join(',') });
    }

    return attributeService.saveEntityAttributes(asset.id, 'SERVER_SCOPE', attributeBody);
  }
}

