actionCellButton.customFunction:
let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let assetService = $injector.get(widgetContext.servicesMap.get('assetService'));
let entityRelationService = $injector.get(widgetContext.servicesMap.get('entityRelationService'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));

const t = widgetContext.custom.t;

openEditEntityDialog();

function openEditEntityDialog() {
  customDialog.customDialog(htmlTemplate, EditEntityDialogController).subscribe();
}

function EditEntityDialogController(instance) {
  let vm = instance;
  vm.ownerId = widgetContext.defaultSubscription.configuredDatasources[0].entity.id;
  vm.ownerLevel = widgetContext.$scope.ownerLevel;
  vm.t = t;
  vm.isSingleReport = additionalParams.reportCategory == 'single';
  vm.customerL1List = [{ name: t('thingplus.selector.entire-customerL1'), value: '' }];
  vm.customerL2List = [{ name: t('thingplus.selector.entire-customerL2'), value: '' }];
  vm.deviceList = [{ name: t('thingplus.selector.select-all'), value: 'ENTIRE' }];
  vm.userList = [{ name: t('thingplus.selector.select-all'), value: 'ENTIRE', email: '' }];
  if (vm.ownerLevel == 0) {
    vm.customerL1List = vm.customerL1List.concat(
      widgetContext.custom.customerL1List
        .map(x => {
          return {
            name: x.name,
            value: x.id.id,
          };
        })
        .sort((a, b) => {
          if (a.name > b.name) return 1;
          if (a.name < b.name) return -1;
          return 0;
        })
    );
  }
  if (vm.ownerLevel <= 1) {
    vm.customerL2List = vm.customerL2List.concat(
      widgetContext.custom.customerL2List
        .map(x => {
          return {
            name: x.name,
            value: x.id.id,
          };
        })
        .sort((a, b) => {
          if (a.name > b.name) return 1;
          if (a.name < b.name) return -1;
          return 0;
        })
    );
  }
  vm.userList = vm.userList.concat(
    widgetContext.custom.userList
      .map(x => {
        return {
          name: x.details.firstName,
          value: x.id.id,
          email: x.details.email,
        };
      })
      .sort((a, b) => {
        if (a.name > b.name) return 1;
        if (a.name < b.name) return -1;
        return 0;
      })
  );

  vm.deviceList = [];
  vm.emailList = [];
  if (additionalParams.receiver) {
    let originReceiver = additionalParams.receiver.split(',');
    vm.emailList = vm.userList
      .filter(x => originReceiver.includes(x.email))
      .map(x => {
        return { name: x.name, value: x.email };
      });
    vm.emailList = vm.emailList.concat(
      originReceiver
        .filter(x => !vm.userList.map(y => y.email).includes(x))
        .map(x => {
          return { value: x };
        })
    );
  }
  if (additionalParams.device) {
    vm.deviceList = additionalParams.device.split(',').map(x => widgetContext.custom.relations[x].label);
  }
  const REPORT_CATEGORY = {
    entire: t('thingplus.page.report.report-entire'),
    single: t('thingplus.page.report.report-single'),
  };
  const REPORT_DASHBOARD = {
    Report: t('thingplus.page.report.entire-report'),
    'Total Work Report': t('thingplus.page.report.total-work-report'),
    'Cost Report': t('thingplus.page.report.cost-report'),
    'Work Report': t('thingplus.page.report.work-report'),
    'Delay Report': t('thingplus.page.report.delay-report'),
  };
  const REPORT_TYPE = {
    DAILY: t('thingplus.time-format.day-by-day'),
    WEEKLY: t('thingplus.time-format.week-by-week'),
    MONTHLY: t('thingplus.time-format.month-by-month'),
    CUSTOM: t('thingplus.label.custom-input'),
  };
  let dayList = [
    { name: t('thingplus.time-format.weekday-short.mon'), value: 0 },
    { name: t('thingplus.time-format.weekday-short.tue'), value: 1 },
    { name: t('thingplus.time-format.weekday-short.wed'), value: 2 },
    { name: t('thingplus.time-format.weekday-short.thu'), value: 3 },
    { name: t('thingplus.time-format.weekday-short.fri'), value: 4 },
    { name: t('thingplus.time-format.weekday-short.sat'), value: 5 },
    { name: t('thingplus.time-format.weekday-short.sun'), value: 6 },
  ];

  let customerL1Index = vm.customerL1List.findIndex(x => x.value == additionalParams.customerL1);
  let customerL1Name = customerL1Index > -1 ? vm.customerL1List[customerL1Index].name : '';
  let customerL2Index = vm.customerL2List.findIndex(x => x.value == additionalParams.customerL2);
  let customerL2Name = customerL2Index > -1 ? vm.customerL2List[customerL2Index].name : '';

  let reportTime = moment().startOf('day').valueOf() + Number(additionalParams.reportTime);
  let reportDate = additionalParams.reportContent;
  if (additionalParams.reportType == 'DAILY') {
    let dailyContent = additionalParams.reportContent
      .split(',')
      .map(x => dayList[x].name)
      .join(',');
    reportDate = dailyContent;
  }
  if (additionalParams.reportType == 'WEEKLY') {
    reportDate = dayList[additionalParams.reportContent].name;
  }
  vm.detailEntityFormGroup = vm.fb.group({
    title: [additionalParams.name, [vm.validators.required]],
    reportCategory: [REPORT_CATEGORY[additionalParams.reportCategory]],
    reportDashboard: [REPORT_DASHBOARD[additionalParams.reportDashboard]],
    customerL1Name: [customerL1Name],
    customerL2Name: [customerL2Name],
    reportType: [REPORT_TYPE[additionalParams.reportType]],
    reportTime: [moment(reportTime).format('HH:mm')],
    reportDate: [reportDate],
  });

  vm.calcFontSize = function () {
    const originWidth = widgetContext.settings.widget.originWidth;
    let widgetFontSize = _.round((widgetContext.width / originWidth) * 10, 2);
    if (widgetFontSize < 6.25) {
      widgetFontSize = 6.25;
    }
    return widgetFontSize;
  };
  vm.cancel = function () {
    vm.dialogRef.close(null);
  };
}

