actionCellButton.customFunction:
let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let assetService = $injector.get(widgetContext.servicesMap.get('assetService'));
let entityRelationService = $injector.get(widgetContext.servicesMap.get('entityRelationService'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));

const t = widgetContext.custom.t;

openEditEntityDialog();

function openEditEntityDialog() {
  customDialog.customDialog(htmlTemplate, EditEntityDialogController).subscribe();
}

function EditEntityDialogController(instance) {
  let vm = instance;
  vm.ownerId = widgetContext.defaultSubscription.configuredDatasources[0].entity.id;
  vm.ownerLevel = widgetContext.$scope.ownerLevel;
  vm.t = t;
  vm.currentStep = 0;
  vm.newEmail = '';
  vm.emailList = [];
  vm.isSingleReport = additionalParams.reportCategory == 'single';
  vm.customerL1List = [{ name: t('thingplus.selector.entire-customerL1'), value: '' }];
  vm.customerL2List = [{ name: t('thingplus.selector.entire-customerL2'), value: '' }];
  vm.deviceList = [{ name: t('thingplus.selector.select-all'), value: 'ENTIRE' }];
  vm.userList = [{ name: t('thingplus.selector.select-all'), value: 'ENTIRE', email: '' }];
  if (vm.ownerLevel == 0) {
    vm.customerL1List = vm.customerL1List.concat(
      widgetContext.custom.customerL1List
        .map(x => {
          return {
            name: x.name,
            value: x.id.id,
          };
        })
        .sort((a, b) => {
          if (a.name > b.name) return 1;
          if (a.name < b.name) return -1;
          return 0;
        })
    );
  }
  if (vm.ownerLevel <= 1) {
    vm.customerL2List = vm.customerL2List.concat(
      widgetContext.custom.customerL2List
        .map(x => {
          return {
            name: x.name,
            value: x.id.id,
          };
        })
        .sort((a, b) => {
          if (a.name > b.name) return 1;
          if (a.name < b.name) return -1;
          return 0;
        })
    );
  }
  vm.deviceList = vm.deviceList.concat(
    widgetContext.custom.deviceList
      .map(x => {
        return {
          name: x.label,
          value: x.id.id,
        };
      })
      .sort((a, b) => {
        if (a.name > b.name) return 1;
        if (a.name < b.name) return -1;
        return 0;
      })
  );
  vm.userList = vm.userList.concat(
    widgetContext.custom.userList
      .map(x => {
        return {
          name: x.details.firstName,
          value: x.id.id,
          email: x.details.email,
        };
      })
      .sort((a, b) => {
        if (a.name > b.name) return 1;
        if (a.name < b.name) return -1;
        return 0;
      })
  );
  vm.originDevices = [];
  vm.originUsers = [];
  vm.emailList = [];
  if (additionalParams.receiver) {
    let originReceiver = additionalParams.receiver.split(',');
    vm.originUsers = vm.userList.filter(x => originReceiver.includes(x.email)).map(x => x.value);
    vm.emailList = originReceiver.filter(x => !vm.userList.map(x => x.email).includes(x));
  }
  if (additionalParams.device) {
    vm.originDevices = additionalParams.device.split(',');
  }
  vm.reportCategoryList = [
    { name: t('thingplus.page.report.report-entire'), value: 'entire' },
    { name: t('thingplus.page.report.report-single'), value: 'single' },
  ];
  if (vm.isSingleReport) {
    vm.reportDashboardList = [
      { name: t('thingplus.page.report.work-report'), value: 'Work Report' },
      { name: t('thingplus.page.report.delay-report'), value: 'Delay Report' },
    ];
  } else {
    vm.reportDashboardList = [
      { name: t('thingplus.page.report.entire-report'), value: 'Report' },
      { name: t('thingplus.page.report.total-work-report'), value: 'Total Work Report' },
      { name: t('thingplus.page.report.cost-report'), value: 'Cost Report' },
    ];
  }

  vm.dayList = [
    { name: t('thingplus.time-format.weekday-long.mon'), value: 0 },
    { name: t('thingplus.time-format.weekday-long.tue'), value: 1 },
    { name: t('thingplus.time-format.weekday-long.wed'), value: 2 },
    { name: t('thingplus.time-format.weekday-long.thu'), value: 3 },
    { name: t('thingplus.time-format.weekday-long.fri'), value: 4 },
    { name: t('thingplus.time-format.weekday-long.sat'), value: 5 },
    { name: t('thingplus.time-format.weekday-long.sun'), value: 6 },
  ];
  vm.dateList = [];
  for (let i = 1; i <= 31; i++) {
    vm.dateList.push({ name: t('thingplus.time-format.day-value', { day: i }), value: i });
  }
  let customerL1Name = t('thingplus.selector.entire-customerL1');
  if (additionalParams.customerL1 && additionalParams.customerL1 != '') {
    customerL1Name = widgetContext.custom.relations[additionalParams.customerL1].name;
  }
  let customerL2Name = t('thingplus.selector.entire-customerL2');
  if (additionalParams.customerL2 && additionalParams.customerL2 != '') {
    customerL2Name = widgetContext.custom.relations[additionalParams.customerL2].name;
  }
  let dailyContent = [true, true, true, true, true, true, true];
  if (additionalParams.reportType == 'DAILY') {
    dailyContent = [false, false, false, false, false, false, false];
    let list = additionalParams.reportContent.split(',');
    for (let i in list) {
      if (list[i]) {
        dailyContent[list[i]] = true;
      }
    }
  }
  let reportTime = moment().startOf('day').valueOf() + Number(additionalParams.reportTime);
  vm.editEntityFormGroup = vm.fb.group({
    title: [additionalParams.name, [vm.validators.required]],
    reportCategory: [additionalParams.reportCategory],
    reportDashboard: [additionalParams.reportDashboard],
    customerL1Name: [customerL1Name],
    customerL2Name: [customerL2Name],
    device: [vm.originDevices],
    reportType: [additionalParams.reportType],
    reportTime: [moment(reportTime).toDate()],
    dailyContent: vm.fb.group({
      0: [dailyContent[0]],
      1: [dailyContent[1]],
      2: [dailyContent[2]],
      3: [dailyContent[3]],
      4: [dailyContent[4]],
      5: [dailyContent[5]],
      6: [dailyContent[6]],
    }),
    weeklyContent: [additionalParams.reportType == 'WEEKLY' ? additionalParams.reportContent : 0],
    monthlyContent: [additionalParams.reportType == 'MONTHLY' ? additionalParams.reportContent : 1],
    customContent: [additionalParams.reportType == 'CUSTOM' ? additionalParams.reportContent : 2],
    user: [vm.originUsers],
    newEmail: [''],
  });
  if (vm.ownerLevel == 0) {
    vm.editEntityFormGroup.controls.customerL1 = vm.fb.control(additionalParams.customerL1);
  }
  if (vm.ownerLevel <= 1) {
    vm.editEntityFormGroup.controls.customerL2 = vm.fb.control(additionalParams.customerL2);
  }

  vm.calcFontSize = function () {
    const originWidth = widgetContext.settings.widget.originWidth;
    let widgetFontSize = _.round((widgetContext.width / originWidth) * 10, 2);
    if (widgetFontSize < 6.25) {
      widgetFontSize = 6.25;
    }
    return widgetFontSize;
  };
  vm.cancel = function () {
    vm.dialogRef.close(null);
  };
  vm.save = function () {
    vm.editEntityFormGroup.markAsPristine();
    let users = vm.editEntityFormGroup.get('user').value;
    if (vm.emailList.length == 0 && users.length == 0) {
      window.alert(t('thingplus.help.error-required-receiver'));
      return;
    }
    getAsset().subscribe(asset => {
      widgetContext.rxjs.forkJoin([saveAsset(asset), saveAttribute(asset)]).subscribe(() => {
        widgetContext.updateAliases();
        vm.dialogRef.close(null);
      });
    });
  };
  vm.selectStep = function (e, index) {
    vm.currentStep = index;
  };
  vm.prevStep = function () {
    vm.currentStep--;
  };
  vm.nextStep = function () {
    vm.currentStep++;
  };
  vm.setCustomerL1 = function (e) {
    let customerL1 = vm.editEntityFormGroup.get('customerL1').value;
    vm.customerL2List = [{ name: t('thingplus.selector.entire-customerL2'), value: '' }];
    vm.deviceList = [{ name: t('thingplus.selector.select-all'), value: 'ENTIRE' }];
    vm.editEntityFormGroup.patchValue(
      {
        customerL2: '',
        device: [],
      },
      { emitEvent: false }
    );
    vm.customerL2List = vm.customerL2List.concat(
      widgetContext.custom.customerL2List
        .filter(x => {
          return customerL1 == '' || x.parent.id.id == customerL1;
        })
        .map(x => {
          return {
            name: x.name,
            value: x.id.id,
          };
        })
        .sort((a, b) => {
          if (a.name > b.name) return 1;
          if (a.name < b.name) return -1;
          return 0;
        })
    );
    vm.deviceList = vm.deviceList.concat(
      widgetContext.custom.deviceList
        .filter(x => {
          return customerL1 == '' || x.parent.parent.id.id == customerL1;
        })
        .map(x => {
          return {
            name: x.label,
            value: x.id.id,
          };
        })
        .sort((a, b) => {
          if (a.name > b.name) return 1;
          if (a.name < b.name) return -1;
          return 0;
        })
    );
  };
  vm.setCustomerL2 = function (e) {
    let customerL1 = vm.editEntityFormGroup.get('customerL1').value;
    let customerL2 = vm.editEntityFormGroup.get('customerL2').value;
    vm.deviceList = [{ name: t('thingplus.selector.select-all'), value: 'ENTIRE' }];
    vm.editEntityFormGroup.patchValue(
      {
        device: [],
      },
      { emitEvent: false }
    );
    vm.deviceList = vm.deviceList.concat(
      widgetContext.custom.deviceList
        .filter(x => {
          return (
            (customerL2 == '' && (customerL1 == '' || x.parent.parent.id.id == customerL1)) ||
            x.parent.id.id == customerL2
          );
        })
        .map(x => {
          return {
            name: x.label,
            value: x.id.id,
          };
        })
        .sort((a, b) => {
          if (a.name > b.name) return 1;
          if (a.name < b.name) return -1;
          return 0;
        })
    );
  };
  vm.setDevice = function (e) {
    //  Entire   
    let newValue = _.cloneDeep(e.value);
    if (vm.originDevices.includes('ENTIRE')) {
      // Entire     
      if (!e.value.includes('ENTIRE')) {
        newValue = [];
        // Entire     Entire 
      } else if (e.value.length < vm.originDevices.length) {
        newValue = e.value.filter(x => x != 'ENTIRE');
      }
      //  Entire    
    } else {
      // Entire    
      if (e.value.includes('ENTIRE')) {
        newValue = vm.deviceList.map(x => x.value);
      }
    }
    vm.editEntityFormGroup.patchValue(
      {
        device: newValue,
      },
      { emitEvent: false }
    );
    vm.originDevices = newValue;
  };
  vm.setUser = function (e) {
    //  Entire   
    let newValue = _.cloneDeep(e.value);
    if (vm.originUsers.includes('ENTIRE')) {
      // Entire     
      if (!e.value.includes('ENTIRE')) {
        newValue = [];
        // Entire     Entire 
      } else if (e.value.length < vm.originUsers.length) {
        newValue = e.value.filter(x => x != 'ENTIRE');
      }
      //  Entire    
    } else {
      // Entire    
      if (e.value.includes('ENTIRE')) {
        newValue = vm.userList.map(x => x.value);
      }
    }
    vm.editEntityFormGroup.patchValue(
      {
        user: newValue,
      },
      { emitEvent: false }
    );
    vm.originUsers = newValue;
  };
  vm.setReportCategory = function (e) {
    if (e.value == 'single') {
      vm.isSingleReport = true;
      vm.reportDashboardList = [
        { name: t('thingplus.page.report.work-report'), value: 'Work Report' },
        { name: t('thingplus.page.report.delay-report'), value: 'Delay Report' },
      ];
      vm.editEntityFormGroup.patchValue(
        {
          reportDashboard: 'Work Report',
        },
        { emitEvent: false }
      );
    } else if (e.value == 'entire') {
      vm.isSingleReport = false;
      vm.reportDashboardList = [
        { name: t('thingplus.page.report.entire-report'), value: 'Report' },
        { name: t('thingplus.page.report.total-work-report'), value: 'Total Work Report' },
        { name: t('thingplus.page.report.cost-report'), value: 'Cost Report' },
      ];
      vm.editEntityFormGroup.patchValue(
        {
          reportDashboard: 'Report',
        },
        { emitEvent: false }
      );
    }
  };
  vm.setReportType = function (e, type) {
    vm.editEntityFormGroup.patchValue(
      {
        reportType: type,
      },
      { emitEvent: false }
    );
  };
  vm.addEmail = function (e) {
    let newEmail = vm.editEntityFormGroup.get('newEmail');
    if (newEmail.hasError('email')) return;
    if (newEmail.value != '') {
      vm.emailList.push(newEmail.value);
      vm.editEntityFormGroup.patchValue(
        {
          newEmail: '',
        },
        { emitEvent: false }
      );
    }
  };
  vm.deleteEmail = function (e, email) {
    let targetIndex = vm.emailList.indexOf(email);
    vm.emailList.splice(targetIndex, 1);
  };

  function getAsset() {
    return assetService.getAsset(entityId.id);
  }
  function saveAsset(asset) {
    asset.label = vm.editEntityFormGroup.get('title').value;
    return assetService.saveAsset(asset);
  }

  function saveAttribute(asset) {
    let title = vm.editEntityFormGroup.get('title').value;
    let reportCategory = vm.editEntityFormGroup.get('reportCategory').value;
    let reportDashboard = vm.editEntityFormGroup.get('reportDashboard').value;
    let customerL1Id = '',
      customerL2Id = '';
    if (vm.ownerLevel == 0) {
      customerL1Id = vm.editEntityFormGroup.get('customerL1').value;
      customerL2Id = vm.editEntityFormGroup.get('customerL2').value;
    } else if (vm.ownerLevel == 1) {
      customerL1Id = entityId.id;
      customerL2Id = vm.editEntityFormGroup.get('customerL2').value;
    } else {
      customerL2Id = entityId.id;
      customerL1Id = widgetContext.custom.relations[customerL2Id].parent.id.id;
    }
    let reportType = vm.editEntityFormGroup.get('reportType').value;
    let reportTime = vm.editEntityFormGroup.get('reportTime').value;
    reportTime = moment(reportTime).hour() * 3600000 + moment(reportTime).minute() * 60000;
    let reportContent = '';
    if (reportType == 'DAILY') {
      reportContent = vm.editEntityFormGroup.get('dailyContent').value;
      let result = [];
      for (let i in reportContent) {
        if (reportContent[i]) {
          result.push(i);
        }
      }
      reportContent = result.join(',');
    } else if (reportType == 'WEEKLY') {
      reportContent = vm.editEntityFormGroup.get('weeklyContent').value;
    } else if (reportType == 'MONTHLY') {
      reportContent = vm.editEntityFormGroup.get('monthlyContent').value;
    } else {
      reportContent = vm.editEntityFormGroup.get('customContent').value;
    }
    let reportPage = [
      {
        dashboardId: widgetContext.custom.dashboardList[reportDashboard].id.id,
        stateId: 'default',
      },
    ];

    let users = vm.editEntityFormGroup.get('user').value;
    users = users.filter(x => x != 'ENTIRE');

    let emailList = vm.userList.filter(x => users.includes(x.value)).map(x => x.email);
    emailList = emailList.concat(vm.emailList);
    emailList = _.uniq(emailList);

    let attributeBody = [
      { key: 'name', value: title },
      { key: 'reportCategory', value: reportCategory },
      { key: 'reportDashboard', value: reportDashboard },
      { key: 'customerL1', value: customerL1Id },
      { key: 'customerL2', value: customerL2Id },
      { key: 'reportType', value: reportType },
      { key: 'reportTime', value: reportTime },
      { key: 'reportContent', value: reportContent },
      { key: 'reportPage', value: reportPage },
      { key: 'reportUpdateTime', value: moment().valueOf() },
      { key: 'receiver', value: emailList.join(',') },
    ];

    if (vm.isSingleReport) {
      let deviceIds = vm.editEntityFormGroup.get('device').value;
      deviceIds = deviceIds.filter(x => x != 'ENTIRE');
      attributeBody.push({ key: 'device', value: deviceIds.join(',') });
    }

    return attributeService.saveEntityAttributes(asset.id, 'SERVER_SCOPE', attributeBody);
  }
}

